import React,{useState} from 'react'

function To() {
    const [item,setItem]=useState([])
    const [input,setInput]=useState('')
    //add item
    const addItem=(e)=>(
        e.preventDefault(),
        setItem([...item,{
            id:item.length,
            text:input,
            completed:false
        }]),
        setInput('')

    )
    //Delete item
  return (
    <div>
        <h2>
            To Do List
        </h2>
        <form>

                <div>
                    <input type='text' value={input} onChange={e=>setInput(e.target.value)}></input>
                </div>
                <div>
                    <button onClick={addItem}>ADD ITEM</button>
                </div>
                <ul>
                    {item.map(item=>(
                        <li>{item.text} <button onc>Delete</button></li>
                    ))}
                </ul>
                
        </form>
    </div>
  )
}

export default To
